import {
  ADD_Course,
  DELETE_Course,
  ITEMS_LOADING,
  GET_Course
} from '../actions/types';

const initialState = {
  courses: [],
  loading: false
};

export default function(state=initialState, action){
  switch(action.type){
    case GET_Course:
      return {
        ...state
      }
    default:
      return state;  
  }
}
